import './Button.scss';
import vector from'../../pages/images/Vector.png'

import React from 'react';

import Button from '@material-ui/core/Button';
import { createTheme, ThemeProvider } from '@material-ui/core/styles';


function ButtonComponent({nameBtn, color, buttonClick}){ 

  const theme = createTheme({
    palette: {
      primary: {
        main: {color},
      },
    },
  });

    return (
      <>
      <ThemeProvider theme={theme}>
        <Button variant="contained" color="primary" onClick={buttonClick}>
          {nameBtn}
        </Button>
      </ThemeProvider>
        {/* <button className={classBtn} onClick={buttonClick} style={showButton}><img src={vector} alt="logo KPis"/>{nameBtn}</button> */}
      </>
    );
  };
  export default ButtonComponent;
