import './Form.scss';
import React, {useState, useEffect} from 'react';
import ButtonComponent from '../../components/button-component/ButtonComponent';
import FormGroup from '@material-ui/core/FormGroup'

function Form({title, question, answer, coment, showTag, showButtons, id, setValue=()=>{}}){

  const buttonNumber = [0,1,2,3,4,5,7,8,9,10];
  const [btn, setBnt] = useState('');
  const [textArea, setTextArea] = useState('');

  useEffect(() => {
   setValue(!!(btn && textArea))
  }, [btn, textArea])

    return (
      <>
        <div className ="text-form-container">
          <div className="text-form">
            <h1 className="title-form">{title}</h1>
              <p>{question}</p>

              <div className="button-order">
                <p>{answer}</p>
                <div className="btnQuestions" style={showButtons}>
                {buttonNumber.map((number, indice)=>(
                  <ButtonComponent key={indice} nameBtn={number} variant="outlined" onClick={({target})=> setBnt(target.value)}/>
                ))}
                </div>               
              </div>

              <div className="area-coment" style={showTag}>
                <p>{coment}</p>
                <textarea id={id}placeholder="Digite sua resposta" onChange={({ target }) => setTextArea(target.value)}></textarea>
              </div>
          </div>           
        </div>
      </>
    );
  };
  export default Form;
